        city: "",
        travelDate: "",
        duration: "6",
        travelEndDate: "",
        localeTravelDate: "",
        localeTravelEndDate: "",
        days: [],
      },
      errors: [],
    };
  },

  computed: {
    ...mapGetters(["isAllGood"]),
  },

  methods: {
    ...mapActions({
      newTravelData: "newTravelData",
      changeAllGoodStatus: "changeAllGoodStatus",
      newWeatherData: "newWeatherData",
      newListData: "newListData",
      newGuideData: "newGuideData",
    }),

    selectType(index) {
      this.isClicked = index;
      this.travelData.selectedType = index;
    },

    async sendtravelData() {
      if (
        this.travelData.selectedType >= 0 &&
        this.travelData.city !== "" &&
        this.travelData.travelDate !== ""
      ) {
        await this.findEndDate();
        await this.newTravelData(this.travelData); // отправляю полученные данные о поездки в хранилище vuex

        await this.getWeather(); // получаю данные о погоде, сортирую потом отправляю в vuex

        this.changeAllGoodStatus(true); //

        if (this.isAllGood == true) {
          this.$router.push("/weather");
        }
        this.notice = false;
        // добавляем данные для листа
        this.newListData(this.travelData.selectedType);

        // добавляем данные для гида
        const dataForGuide = {
          type: this.travelData.selectedType,
          city: this.travelData.city,
        };
        this.newGuideData(dataForGuide);
      } else {
        this.notice = !this.notice;
        setTimeout(() => {
          this.notice = false;
        }, 3000);
      }
    },

    findEndDate() {
      const date = new Date(this.travelData.travelDate); // начальная дата
      const duration = this.travelData.duration; // количество дней / период поездки
      date.setDate(date.getDate() + Number(duration)); // конечная дата периода

      this.travelData.travelEndDate = date.toISOString().slice(0, 10);

      // переводим время в локальное
      const options = { month: "long", day: "numeric" }; // опции для метода localeDate

      this.travelData.localeTravelDate = new Date(
        this.travelData.travelDate
      ).toLocaleDateString("ru", options);
      this.travelData.localeTravelEndDate = new Date(
        this.travelData.travelEndDate
      ).toLocaleDateString("ru", options);

      // заодно находим все дни для последующего использования
      const getDays = () => {
        const date = new Date(this.travelData.travelDate); // начальная дата
        const duration = Number(this.travelData.duration); // количество дней / период поездки
        const options = { month: "long", day: "numeric" };

        for (let i = 1; i <= duration; i++) {
          const n = i;
          date.setDate(date.getDate() + 1); // конечная дата периода

          const localeDate = new Date(date).toLocaleDateString("ru", options);
          this.travelData.days.push(localeDate);
        }
      };
      getDays();
    },

    // Получаем погоду
    async getWeather() {
      await axios
        .get(
          `https://api.openweathermap.org/data/2.5/forecast?q=${this.travelData.city}&lang=ru&appid=aa5001c166de783d663ea84db1abfa1a&units=metric`
        )

        .then((responce) => {
          const Data = responce.data.list;
          const weatherData = [];

          for (let i = 0; i < 40; i++) {
            const obj = {
              temperature: Math.round(Data[i].main.temp),
              date: Data[i].dt_txt,
              weather: Data[i].weather[0].description,
              icon: Data[i].weather[0].icon,
            };
            // взяли только необходимые нам данные
            weatherData.push(obj);
          } // а это погода именно сейчас, для след. страницы
          const weatherNow = {
            temperature: Math.round(Data[0].main.temp),
            date: Data[0].dt_txt,
            weather: Data[0].weather[0].description,
            icon: Data[0].weather[0].icon,
          };

          weatherData.unshift(weatherNow);

          // дальше группирую по одному дню // апи нам дает погоду на каждые 3 часа, поэтому пришлось.
          const result = {};

          weatherData.forEach((item) => {
            const day = item.date.slice(0, -9); // вырезаем время из строки
            if (!result[day]) {
              result[day] = { ...item }; // создаем новую запись
            } else {
              if (item.temperature > result[day].temperature) {
                result[day].temperature = item.temperature;
                result[day].weather = item.weather;
                result[day].icon = item.icon;
              }
            }
          });

          const finalResult = Object.values(result); // преобразуем объект в массиd
          // погода дается на 5-6 дней бесплатно, поэтому просто последний день добавляю еще несколько раз
          const lastDay = finalResult[finalResult.length - 1];

          if (finalResult.length - 1 <= Number(this.travelData.duration)) {
            const x = Number(this.travelData.duration) - finalResult.length;
            for (let i = 0; i < x; i++) {
              finalResult.push(lastDay);
            }
          }
          this.newWeatherData(finalResult); // передали в хранилище
        })
        .catch((e) => {
          this.errors.push(e);
        });
    },
  },

  mounted() {
    // начальная дата
    this.minDate = new Date().toISOString().slice(0, 10); // при создание приложение беру и устанавливая минимальную дату для календаря
    // конечная дата периода
    const date = new Date(this.minDate);
    date.setDate(date.getDate() + Number(30));
    this.maxDate = date.toISOString().slice(0, 10);
  
    this.travelData.travelDate = this.minDate;
    this.travelData.selectedType = 2;
    this.isClicked = 2;

    this.clientHeight = document.documentElement.clientHeight;
  },

  watch: {
    isClicked() {
      if (this.isClicked == 1) {
        this.cities = ["Самара", "Казань"];
      } else if (this.isClicked == 2) {
        this.cities = ["Барнаул", "Симферополь", "Астрахань"];
      } else if (this.isClicked == 3) {
        this.cities = ["Самара"];
      } else if (this.isClicked == 4) {
        this.cities = ["Москва"];
      } else {
        this.cities = [];
      }
    },
  },
};
</script>

